mapper.select:
select * from store_in_info where store_in_info_seq > 0

	and store_code = #{store_code}


	and w_out_box_barcode = #{w_out_box_barcode}

order by reg_date desc limit 100

select  w_out_box_barcode, style, color, size , count(w_out_box_barcode) as amount from store_in_info where store_in_info_seq > 0

	and store_code = #{store_code}


	and w_out_box_barcode = #{w_out_box_barcode}

group by w_out_box_barcode, style, color, size
order by reg_date desc limit 100

mapper.update:

	update rfid_info
	set status = '6'
	,upd_date = now()
	,upd_user_id = #{upd_user_id}
	, store_in_date = date_format(now(), '%Y-%m-%d')
	  where epc in (select epc from store_in_info where w_out_box_barcode =  #{w_out_box_barcode}
        and style =  #{style}
		)
